import { useDispatch, useSelector } from 'react-redux'

import { multiLanguageActions } from '../../../redux/actions'
import { languageResults } from '../../../redux/selectors'

import PropTypes from 'prop-types'
import {
    Button,
    Menu,
    MenuItem,
    Box} from '@material-ui/core'

import { useStyles } from './style'

import TranslateIcon from '@material-ui/icons/Translate'

const MultiLanguage = props => {  

  const dispatch = useDispatch()

  const language = useSelector(state => languageResults(state))

  const classes = useStyles()

  useEffect(() => {
    if(!language) {
      dispatch(multiLanguageActions(anchor))
    }
  })

  const [anchor, setAnchor] = React.useState(null);
  const [selectedLanguage, setSelectedLanguage] = React.useState(language);

  const handleClick = (event) => {
    setAnchor(event.currentTarget);
  }

  const handleSelectLanguage = (data) => {
    console.log(data)
    handleClose
  }

  const handleClose = (value) => {
    setAnchor(null);
  }

  return(
    <React.Fragment>
        <Box>
            <Button className={classes.options} aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                <TranslateIcon  style={{marginRight: '5px'}} /> {selectedLanguage}
            </Button>
            <Menu
            id="simple-menu"
            anchorEl={anchor}
            keepMounted
            open={Boolean(anchor)}
            onClose={handleClose}
            >
                <MenuItem onClick={handleSelectLanguage('es')}>ESPAÃ‘OL</MenuItem>
                <MenuItem onClick={handleSelectLanguage('en')}>ENGLISH</MenuItem>
            </Menu>
        </Box>
    </React.Fragment>
  )
}

MultiLanguage.propTypes = {
  items: PropTypes.array.isRequired
}

