import React, { useEffect} from 'react'
import { useDispatch, useSelector } from 'react-redux'

import { multiLanguageActions } from '../../../redux/actions'

import PropTypes from 'prop-types'
import {
    Button,
    Menu,
    MenuItem,
    Box} from '@material-ui/core'

import { useStyles } from './style'

import TranslateIcon from '@material-ui/icons/Translate'

const MultiLanguage = props => {  

  const dispatch = useDispatch()

  const language = useSelector(state => {
    console.log(state)
  })

  const classes = useStyles()

  useEffect(() => {

  })

  const [anchor, setAnchor] = React.useState(null);

  const handleClick = (event) => {
    setAnchor(event.currentTarget);
  }

  const handleClose = () => {
    setAnchor(null);
  }

  return(
    <React.Fragment>
        <Box>
            <Button className={classes.options} aria-controls="simple-menu" aria-haspopup="true" onClick={handleClick}>
                <TranslateIcon  style={{marginRight: '5px'}} /> ESPAÑOL
            </Button>
            <Menu
            id="simple-menu"
            anchorEl={anchor}
            keepMounted
            open={Boolean(anchor)}
            onClose={handleClose}
            >
                <MenuItem onClick={handleClose}>ESPAÑOL</MenuItem>
                <MenuItem onClick={handleClose}>ENGLISH</MenuItem>
            </Menu>
        </Box>
    </React.Fragment>
  )
}

MultiLanguage.propTypes = {
  items: PropTypes.array.isRequired
}

export {MultiLanguage}
